mapper.select:
select 
	sale,
	sc,
	scresult,
	scremarks,
	sp,
	spresult,
	spremarks,
	sf,
	sfa,
	sfaresult,
	sfaremarks,
	tftc,
	tftcl,
	tftclresult,
	tftclremarks,
	tftcs,
	tftcsresult,
	tftcsremarks,
	tftcm,
	tftcmresult,
	tftcmremarks,
	tftcc,
	tftccresult,
	tftccremarks,
	tfm,
	tfml,
	tfmlresult,
	tfmlremarks,
	tfms,
	tfmsresult,
	tfmsremarks,
	tfmm,
	tfmmresult,
	tfmmremarks,
	tfmc,
	tfmcresult,
	tfmcremarks,
	ffm,
	ffmc,
	ffmcresult,
	ffmcremarks,
	ffmc2,
	ffmc2result,
	ffmc2remarks,
	ffmp,
	ffmpresult,
	ffmpremarks,
	ifofm,
	ifofmc,
	ifofmcresult,
	ifofmcremarks,
	ifofmp,
	ifofmpresult,
	ifofmpremarks,
	iffm,
	iffmc,
	iffmcresult,
	iffmcremarks,
	iffms,
	iffmsresult,
	iffmsremarks,
	iffmm,
	iffmmresult,
	iffmmremarks,
	creattime,
	inspectionId
from `dh_inspection`




			and inspectionId=#{inspectionId}


			and sale=#{sale}


			and sc=#{sc}


			and scresult=#{scresult}


			and scremarks=#{scremarks}


			and sp=#{sp}


			and spresult=#{spresult}


			and spremarks=#{spremarks}


			and sf=#{sf}


			and sfa=#{sfa}


			and sfaresult=#{sfaresult}


			and sfaremarks=#{sfaremarks}


			and tftc=#{tftc}


			and tftcl=#{tftcl}


			and tftclresult=#{tftclresult}


			and tftclremarks=#{tftclremarks}


			and tftcs=#{tftcs}


			and tftcsresult=#{tftcsresult}


			and tftcsremarks=#{tftcsremarks}


			and tftcm=#{tftcm}


			and tftcmresult=#{tftcmresult}


			and tftcmremarks=#{tftcmremarks}


			and tftcc=#{tftcc}


			and tftccresult=#{tftccresult}


			and tftccremarks=#{tftccremarks}


			and tfm=#{tfm}


			and tfml=#{tfml}


			and tfmlresult=#{tfmlresult}


			and tfmlremarks=#{tfmlremarks}


			and tfms=#{tfms}


			and tfmsresult=#{tfmsresult}


			and tfmsremarks=#{tfmsremarks}


			and tfmm=#{tfmm}


			and tfmmresult=#{tfmmresult}


			and tfmmremarks=#{tfmmremarks}


			and tfmc=#{tfmc}


			and tfmcresult=#{tfmcresult}


			and tfmcremarks=#{tfmcremarks}


			and ffm=#{ffm}


			and ffmc=#{ffmc}


			and ffmcresult=#{ffmcresult}


			and ffmcremarks=#{ffmcremarks}


			and ffmc2=#{ffmc2}


			and ffmc2result=#{ffmc2result}


			and ffmc2remarks=#{ffmc2remarks}


			and ffmp=#{ffmp}


			and ffmpresult=#{ffmpresult}


			and ffmpremarks=#{ffmpremarks}


			and ifofm=#{ifofm}


			and ifofmc=#{ifofmc}


			and ifofmcresult=#{ifofmcresult}


			and ifofmcremarks=#{ifofmcremarks}


			and ifofmp=#{ifofmp}


			and ifofmpresult=#{ifofmpresult}


			and ifofmpremarks=#{ifofmpremarks}


			and iffm=#{iffm}


			and iffmc=#{iffmc}


			and iffmcresult=#{iffmcresult}


			and iffmcremarks=#{iffmcremarks}


			and iffms=#{iffms}


			and iffmsresult=#{iffmsresult}


			and iffmsremarks=#{iffmsremarks}


			and iffmm=#{iffmm}


			and iffmmresult=#{iffmmresult}


			and iffmmremarks=#{iffmmremarks}


			and creattime=#{creattime}




	order by ${orderStr}


	limit #{start},#{pageSize}

select
sale, sc, scresult, scremarks, sp, spresult, spremarks, sf, sfa, sfaresult, sfaremarks, tftc, tftcl, tftclresult, tftclremarks, tftcs, tftcsresult, tftcsremarks, tftcm, tftcmresult, tftcmremarks, tftcc, tftccresult, tftccremarks, tfm, tfml, tfmlresult, tfmlremarks, tfms, tfmsresult, tfmsremarks, tfmm, tfmmresult, tfmmremarks, tfmc, tfmcresult, tfmcremarks, ffm, ffmc, ffmcresult, ffmcremarks, ffmc2, ffmc2result, ffmc2remarks, ffmp, ffmpresult, ffmpremarks, ifofm, ifofmc, ifofmcresult, ifofmcremarks, ifofmp, ifofmpresult, ifofmpremarks, iffm, iffmc, iffmcresult, iffmcremarks, iffms, iffmsresult, iffmsremarks, iffmm, iffmmresult, iffmmremarks, creattime, inspectionId from `dh_inspection`
and creattime = #{creattime}
order by ${orderStr}
limit #{start},#{pageSize}
